    private void refreshTable() {
        DefaultTableModel model = (DefaultTableModel) tblOrders.getModel();
        // Clear existing rows before adding new ones
        model.setRowCount(0);

        OrdersClass[] ordersList = OrdersClass.getOrdersList(); // Get the ordersList from OrdersClass
        for (int i = 0; i < OrdersClass.getOrderCount(); i++) {
            OrdersClass order = ordersList[i];
            Object[] rowData = {
                model.getRowCount() + 1, // Based on the number of rows in the table
                order.getName(),
                order.getTotalProducts(),
                order.getTotalAmount(),
                // order.getItemList() 
            };
            // Add row data to model table
            model.addRow(rowData);
        }
         txtEarnings.setText("₱" + totalEarnings);
    }
    
    
