    private void bttnViewReceiptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bttnViewReceiptActionPerformed
        int selectedRow = tblOrders.getSelectedRow();  
    
    if (selectedRow >= 0) {
        int orderNo = Integer.parseInt(tblOrders.getValueAt(selectedRow, 0).toString()); 

        OrdersClass selectedOrder = OrdersClass.getOrderByOrderNo(orderNo); 

        if (selectedOrder != null) {
            String receiptText = selectedOrder.getItemList();  
            txtReceipt.setText(receiptText);  
        }
    } else {
        JOptionPane.showMessageDialog(null, "Please select an order to view the receipt.");
    }
    }//GEN-LAST:event_bttnViewReceiptActionPerformed

    private void bttnDeliveredActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bttnDeliveredActionPerformed
          DefaultTableModel model = (DefaultTableModel) tblOrders.getModel();
        int selectedRow = tblOrders.getSelectedRow();
        
        if (selectedRow >= 0) {
            int orderNo = Integer.parseInt(model.getValueAt(selectedRow, 0).toString());
            double orderAmount = Double.parseDouble(model.getValueAt(selectedRow, 3).toString());
            // Add to earnings
            totalEarnings += orderAmount;
            txtEarnings.setText("₱" + totalEarnings);
            // Remove the order from OrdersClass array
            OrdersClass.removeOrderByOrderNo(orderNo);
            model.removeRow(selectedRow);
            txtReceipt.setText(""); 
        } else {
            JOptionPane.showMessageDialog(null, "Please select an order");
        }
    }//GEN-LAST:event_bttnDeliveredActionPerformed

    private void bttnCancelOrderActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bttnCancelOrderActionPerformed
       DefaultTableModel model = (DefaultTableModel) tblOrders.getModel();
        int selectedRow = tblOrders.getSelectedRow();
        
        if (selectedRow >= 0) {
            int orderNo = Integer.parseInt(model.getValueAt(selectedRow, 0).toString());

            // Remove the order from OrdersClass array
            OrdersClass.removeOrderByOrderNo(orderNo);
            model.removeRow(selectedRow);
            txtReceipt.setText("");  
        } else {
            JOptionPane.showMessageDialog(null, "Please select an order to cancel");
        }
    }//GEN-LAST:event_bttnCancelOrderActionPerformed

